// backend/local-dev-server.js
// This file is for local development only and should NOT be deployed to Vercel.
// It allows your Vercel serverless function (api/index.js) to run as a local Express server.

// Load environment variables from .env file for local development
require('dotenv').config();

// Import the Express app from your Vercel serverless function file
const app = require('./api'); // Adjust path if api/index.js is not directly in ./api

const PORT = process.env.PORT || 3001; // Use port 3001 for the backend

app.listen(PORT, () => {
  console.log(`Local backend server running on http://localhost:${PORT}`);
  console.log(`Gemini API Key status: ${process.env.GEMINI_API_KEY ? 'Loaded' : 'MISSING!'}`);
});